import React, { useRef } from "react";
import { Animated,PanResponder, Text, View, StyleSheet, Button, SafeAreaView,Image } from "react-native";


const styles = StyleSheet.create({

    tinyLogo: {
      width: 50,
      height: 50,
    },
  
  });
  




const Apps = () => {

    const fadeAnim = useRef(new Animated.Value(    {
        width: 10, // x,y are Animated.Value
        height: 100,
      })).current;
 

    const fadeIn = () => {

        Animated.timing(fadeAnim, {
            toValue: { width: 0, height: 0 },
            duration: 500
          }).start();
      };
 
  
      const fadeOut = () => {

        Animated.timing(fadeAnim, {
            toValue: { width: 10, height: 10 },
            duration: 500
          }).start();
        }
  
    return (

            <SafeAreaView style={styles.container}>
              <Animated.View
                style={[
                  styles.fadingContainer,
                  {
                    // Bind opacity to animated value
                    opacity: fadeAnim
  
                  }

                ]}
              >

                <Text style={styles.fadingText}>Fading View!</Text>
              </Animated.View>
              <View style={styles.buttonRow}>
              <Image
                  
                  source={require('@expo/snack-static/react-native-logo.png')}
                />
                <Button title="Fade In View" onPress={fadeIn} />
                <Button title="Fade Out View" onPress={fadeOut} />
              </View>
            </SafeAreaView>
        

    
    );


  }




export default Apps;
