import React, { Component } from "react";
import { Animated, Text, View, StyleSheet, Button, SafeAreaView } from "react-native";

class Appli extends Component {
    // fadeAnim will be used as the value for opacity. Initial Value: 0
    state ={
        val : new Animated.Value(100),
        etat_bp : 0,
    };

    function1 = () =>{
    if(etat_bp ==0){
        this.setState({etat_bp : 1});
        fadeIn();
    }

    else{
        this.setState({etat_bp : 0});
        fadeOut();  
    }

    };

    fadeIn = () => {
        // Will change fadeAnim value to 1 in 5 seconds
        Animated.timing(val, {
          toValue: 100,
          duration: 100
        }).start();
      };
    
    fadeOut = () => {
        // Will change fadeAnim value to 0 in 3 seconds
        Animated.timing(val, {
          toValue: 200,
          duration: 500
        }).start();
      };
    

  render() {
    return (
        <SafeAreaView style={styles.container}>
        <Animated.Image
          source={{uri:'https://th.bing.com/th/id/OIP.pKBpBoHgy1jBsa0BcShwSAHaHa?w=186&h=187&c=7&r=0&o=5&dpr=1.25&pid=1.7'}}
          style={[
            styles.style_img,
            {
              // Bind opacity to animated value
              width: val,
              height: val,
            }
          ]}
        >
   
        </Animated.Image>
        <View style={styles.buttonRow}>
          <Button title="Fade In View" onPress={fadeIn} />
  
        </View>
      </SafeAreaView>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center"
  },
  fadingContainer: {
    padding: 20,
    backgroundColor: "powderblue"
  },
  fadingText: {
    fontSize: 28
  },
  buttonRow: {
    flexBasis: 100,
    justifyContent: "space-evenly",
    marginVertical: 16
  },

  style_img: {
    height: 100,
    width: 100,
 
  }

});

export default Appli;
